sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Selected note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.index = idx;
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('updateNote', function(event,data) {
		c.data.notes[$rootScope.index].title = $rootScope.noteTitle;
		c.data.notes[$rootScope.index].note = $rootScope.noteBody;
	});
}

sp_widget.script:
(function() {
	data.notes =[];

	var notes_gr = new GlideRecord('x_snc_createnotes_note');
	notes_gr.addQuery('user', gs.getUserID());
	notes_gr.orderBy('sys_created_on');
	notes_gr.query();

	while(notes_gr.next()){
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, notes_gr, 'number,title,sys_id');
		noteObj.note = notes_gr.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}
})();
